/*---------------------------------------------------------------------------*/
/*                                                                           */
/*                              User Control Task                            */
/*                                                                           */
/*  This task is used to control your robot during the user control phase of */
/*  a VEX Competition.                                                       */
/*                                                                           */
/*  You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

task usercontrol()
{
	float xMovement;
	float	rotMovement;
  // User control code here, inside the loop

  while (true)
  {
  	// Get the dedadbanded x and rot values.
  	if( abs(vexRT[Ch3]) > 15 ) {
  		xMovement = vexRT[Ch3];
		}
		else {
			xMovement = 0;
		}
		if( abs(vexRT[Ch4]) > 15 ) {
			rotMovement = vexRT[Ch2];
		}
		else {
			rotMovement = 0;
		}

		// Arcade Drive
		motor[frontRight] = xMovement + rotMovement;
		motor[backRight] = xMovement + rotMovement;
		motor[frontLeft] = xMovement - rotMovement;
		motor[backLeft] = xMovement - rotMovement;

		// We set the axis to control the arm and claw
		motor[armMotor] = vexRT[Ch2];
		motor[clawMotor] = vexRT[Ch1];

		//if( vexRT[Btn6U] == 1 ) {
		//	motor[armMotor] = 127;
		//}
		//else if( vexRT[Btn6D] == 1 ) {
		//	motor[armMotor] = -127;
		//}
		//else {
		//	motor[armMotor] = 15;
		//}

		//if( vexRT[Btn5U] == 1 ) {
		//	motor[clawMotor] = 35;
		//}
		//else if( vexRT[Btn5D] == 1 ) {
		//	motor[clawMotor] = -35;
		//}
		//else {
		//	motor[clawMotor] = 0;
		//}
  }
}
